import React from "react";
import imageHero from "assets/images/img-hero.jpg";
import imghero_frame from "assets/images/img-hero-frame.jpg";
import ic_cities from "assets/images/icons/ic_cities.svg";
import ic_traveler from "assets/images/icons/ic_traveler.svg";
import ic_treasure from "assets/images/icons/ic_treasure.svg";
import formatNumber from "utils/formatNumber";

import Button from "elements/Button";

// (props) untuk memanggil json -- data....
function Hero(props) {
  //membuat fungsi button smooth
  function showMostPicked() {
    window.scrollTo({
      top: props.refMostPicked.current.offsetTop - 30,
      behavior: "smooth",
    });
  }

  return (
    <section className="container">
      <div className="row align-items-center">
        <div className="kiri col-auto pr-5" style={{ width: 530 }}>
          <h1 className=" font-weight-bold line-height-1 mb-3">
            Forget Busy Work, <br /> Start Next Vacation
          </h1>
          <p
            className="mb-4 font-weight-light text-gray-500 w-75"
            style={{ lineHeight: "170%" }}
          >
            We provide what you need to enjoy your holiday with family. Time to
            make another memorable moments.
          </p>
          <Button
            className="btn px-5 "
            hasShadow
            isPrimary
            onClick={showMostPicked}
          >
            {" "}
            Show Me Now
          </Button>

          <div className="row" style={{ marginTop: "80px" }}>
            <div
              className="col-auto"
              style={{
                marginRight: 35,
              }}
            >
              <img
                width="36"
                height="36"
                src={ic_traveler}
                alt={`${props.data.travelers} Travelers`}
              />
              <h6 className="mt-3">
                {formatNumber(props.data.travelers)}{" "}
                <span className="text-gray-500 font-weight-light">
                  travelers
                </span>
              </h6>
            </div>

            <div
              className="col-auto"
              style={{
                marginRight: 35,
              }}
            >
              <img
                width="36"
                height="36"
                src={ic_treasure}
                alt={`${props.data.travelers} Treasures`}
              />
              <h6 className="mt-3">
                {formatNumber(props.data.treasures)}{" "}
                <span className="text-gray-500 font-weight-light">
                  treasures
                </span>
              </h6>
            </div>

            <div className="col-auto">
              <img
                width="36"
                height="36"
                src={ic_cities}
                alt={`${props.data.travelers} Cities`}
              />
              <h6 className="mt-3">
                {formatNumber(props.data.cities)}{" "}
                <span className="text-gray-500 font-weight-light">cities</span>
              </h6>
            </div>
          </div>
        </div>

        <div className="kanan col-6 pl-5" style={{ marginBottom: "-30px" }}>
          <div style={{ width: 520, height: 410 }}>
            <img
              src={imageHero}
              alt="Room With Sources"
              className="img-fluid position-absolute"
              style={{ margin: "-30px 0 0 -30px", zIndex: 1 }}
            />
            <img
              src={imghero_frame}
              alt="Room With Sources frame"
              className="img-fluid position-absolute"
              style={{ margin: "0 -15px -15px 80px" }}
            />
          </div>
        </div>
      </div>
    </section>
  );
}

export default Hero;
